// Get the AI Agent output text
const inputData = $input.first().json;
const text = inputData.output || '';
const chatInput = $('When chat message received').first().json.chatInput || '';

// Parse the text to extract information
let country = 'Sri Lanka'; // Default fallback
let arrivalDate = '';
let adult = 2; // Default 2 adults
let children = 0; // Default 0 children
let places = [];
let mealType = 'BB'; // Default
let cityMealMap = {}; // Map of city to meal type
let hotelStarCategory = 4; // Default 4-star
let totalNights = 8; // Default 8 nights (9 days)

// Extract country (e.g., "Vietnam multi-city tour" or "Sri Lanka multi-city tour")
const countryMatch = text.match(/requesting a ([A-Za-z\s]+?)\s+multi-city tour/i);
if (countryMatch) {
  country = countryMatch[1].trim();
}

// Extract duration (e.g., "for 9 days, 8 nights")
const durationMatch = text.match(/for (\d+) days, (\d+) nights/i);
if (durationMatch) {
  totalNights = parseInt(durationMatch[2]);
} else {
  // Check chat input for days/nights
  const chatDaysMatch = chatInput.match(/(\d+)\s+days/i);
  if (chatDaysMatch) {
    const days = parseInt(chatDaysMatch[1]);
    totalNights = days - 1; // X days = X-1 nights
  }
  const chatNightsMatch = chatInput.match(/(\d+)\s+nights/i);
  if (chatNightsMatch) {
    totalNights = parseInt(chatNightsMatch[1]);
  }
}

// Extract dates (e.g., "from December 12 to December 18, 2025")
const dateMatch = text.match(/from ([A-Za-z]+ \d+) to ([A-Za-z]+ \d+), (\d{4})/i);
if (dateMatch) {
  const monthDay = dateMatch[1]; // e.g., "December 12"
  const year = dateMatch[3];
  const date = new Date(`${monthDay}, ${year}`);
  arrivalDate = date.toISOString().split('T')[0];
} else {
  // Check chat input for date in format YYYY-MM-DD or similar
  const chatDateMatch = chatInput.match(/(\d{4})[-\/](\d{1,2})[-\/](\d{1,2})/i);
  if (chatDateMatch) {
    arrivalDate = `${chatDateMatch[1]}-${chatDateMatch[2].padStart(2, '0')}-${chatDateMatch[3].padStart(2, '0')}`;
  }
}

// If no date found, use today + 15 days as default
if (!arrivalDate) {
  const defaultDate = new Date();
  defaultDate.setDate(defaultDate.getDate() + 15);
  arrivalDate = defaultDate.toISOString().split('T')[0];
}

// Extract pax (e.g., "for 3 adults and 2 children")
const adultMatch = text.match(/(\d+)\s+adult/i);
if (adultMatch) {
  adult = parseInt(adultMatch[1]);
} else {
  // Check chat input
  const chatAdultMatch = chatInput.match(/(\d+)\s+adult/i);
  if (chatAdultMatch) {
    adult = parseInt(chatAdultMatch[1]);
  }
}

const childMatch = text.match(/(\d+)\s+child/i);
if (childMatch) {
  children = parseInt(childMatch[1]);
} else {
  // Check chat input
  const chatChildMatch = chatInput.match(/(\d+)\s+child/i);
  if (chatChildMatch) {
    children = parseInt(chatChildMatch[1]);
  }
}

// Extract hotel star category (e.g., "5-star hotel", "4-star hotel")
const starMatch = text.match(/(\d)-star\s+hotel/i);
if (starMatch) {
  hotelStarCategory = parseInt(starMatch[1]);
}

// Helper function to convert meal plan text to code
function getMealCode(mealText) {
  const lowerText = mealText.toLowerCase();
  if (lowerText.includes('half board')) return 'HB';
  if (lowerText.includes('full board')) return 'FB';
  if (lowerText.includes('all inclusive')) return 'AL';
  if (lowerText.includes('room only')) return 'RO';
  if (lowerText.includes('bed & breakfast') || lowerText.includes('bed and breakfast')) return 'BB';
  return 'BB'; // default
}

// Check if cities have individual meal plans (format: "City1 with meal_plan, City2 with meal_plan")
const cityMealPattern = /([A-Za-z]+)\s+with\s+(half board|full board|room only|all inclusive|bed & breakfast|bed and breakfast)/gi;
const cityMealMatches = text.matchAll(cityMealPattern);
let hasCitySpecificMeals = false;

for (const match of cityMealMatches) {
  const cityName = match[1].trim();
  const mealPlan = match[2].trim();
  cityMealMap[cityName] = getMealCode(mealPlan);
  places.push(cityName);
  hasCitySpecificMeals = true;
}

// If no city-specific meal plans found, extract places and meal plan separately
if (!hasCitySpecificMeals) {
  // Extract places (e.g., "including Colombo, Kandy, Galle, and Yala")
  const placesMatch = text.match(/including ([^.]+)\.?$/i);
  if (placesMatch) {
    const placesStr = placesMatch[1];
    // Split by comma and 'and', then clean up
    places = placesStr
      .split(/,|\s+and\s+/)
      .map(p => p.trim())
      .filter(p => p.length > 0);
  } else {
    // Try to extract from chat input
    const chatPlacesMatch = chatInput.match(/(?:in|to|visit)\s+([A-Za-z,\s]+?)(?:\s+\d|\s+for|$)/i);
    if (chatPlacesMatch) {
      const placesStr = chatPlacesMatch[1];
      places = placesStr
        .split(/,|\s+and\s+/)
        .map(p => p.trim())
        .filter(p => p.length > 2 && !['days', 'nights', 'day', 'night'].includes(p.toLowerCase()));
    }
  }

  // If still no places or empty, default to 3 popular cities
  if (places.length === 0) {
    places = ['Colombo', 'Kandy', 'Galle'];
  }

  // Extract single meal plan for all cities
  if (text.toLowerCase().includes('half board')) {
    mealType = 'HB';
  } else if (text.toLowerCase().includes('full board')) {
    mealType = 'FB';
  } else if (text.toLowerCase().includes('all inclusive')) {
    mealType = 'AL';
  } else if (text.toLowerCase().includes('room only')) {
    mealType = 'RO';
  } else if (text.toLowerCase().includes('bed & breakfast') || text.toLowerCase().includes('bed and breakfast')) {
    mealType = 'BB';
  }
}

// Create the formatted output
const output = {
  country_data: {
    country: country
  },
  genaral_data: {
    arrival_date: arrivalDate,
    total_nights: totalNights,
    pax: {
      adult: adult,
      cwb: children,
      cnb: 0
    },
    market: 1,
    hotel_star_category: hotelStarCategory
  },
  destination_data: {
    place: places,
    meal_type: mealType,
    city_meal_map: Object.keys(cityMealMap).length > 0 ? cityMealMap : null
  }
};

return { json: output };

