const items = $input.all();
const originalData = $('Code').first().json;

// Get arrival date and calculate stays
const arrivalDate = originalData.genaral_data.arrival_date;
const totalNights = originalData.genaral_data.total_nights || 8; // Get from Code node or default to 8
const cityCount = items.length;
const nightsPerCity = Math.floor(totalNights / cityCount);
const remainderNights = totalNights % cityCount; // Handle remainder nights

// Get city-specific meal plans if available
const cityMealMap = originalData.destination_data.city_meal_map;
const defaultMealType = originalData.destination_data.meal_type;

// Get hotel star category
const hotelStarCategory = originalData.genaral_data.hotel_star_category || 4;

// Process each item and calculate check-in/check-out dates
const results = [];
let currentCheckIn = new Date(arrivalDate);

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const currentPlace = item.json['destination_data.place'];

  // Distribute remainder nights to first cities
  const nightsForThisCity = nightsPerCity + (i < remainderNights ? 1 : 0);

  // Calculate check-out date (check-in + nights for this city)
  const checkOutDate = new Date(currentCheckIn);
  checkOutDate.setDate(checkOutDate.getDate() + nightsForThisCity);

  // Format dates as YYYY-MM-DD
  const checkIn = currentCheckIn.toISOString().split('T')[0];
  const checkOut = checkOutDate.toISOString().split('T')[0];

  // Determine meal type for this city
  let mealType = defaultMealType;
  if (cityMealMap && cityMealMap[currentPlace]) {
    mealType = cityMealMap[currentPlace];
  }

  results.push({
    json: {
      current_city: currentPlace,
      country_data: originalData.country_data,
      genaral_data: {
        pax: originalData.genaral_data.pax,
        market: originalData.genaral_data.market
      },
      destination_data: {
        meal_type: mealType,
        star_rate: hotelStarCategory,
        current_place: currentPlace,
        check_in: checkIn,
        check_out: checkOut,
        nights: nightsForThisCity
      }
    }
  });

  // Next city's check-in is current city's check-out
  currentCheckIn = checkOutDate;
}

// Add arrival_date to the first item only
if (results.length > 0) {
  results[0].json.genaral_data.arrival_date = arrivalDate;
}

return results;

