// Get all items from Map IDs Activities
const items = $input.all();

// First: Group by city to process each city separately
const cityGroups = {};

for (const item of items) {
  const cityId = item.json.City;
  if (!cityGroups[cityId]) {
    cityGroups[cityId] = [];
  }
  cityGroups[cityId].push(item.json);
}

// Process each city and assign activity days
const results = [];

for (const cityId in cityGroups) {
  const cityActivities = cityGroups[cityId];

  if (cityActivities.length === 0) continue;

  // Get check-in and check-out dates from first activity
  const checkIn = new Date(cityActivities[0].check_in);
  const checkOut = new Date(cityActivities[0].check_out);

  // Calculate number of nights (activities = nights, not nights + 1)
  const nightCount = Math.round((checkOut - checkIn) / (1000 * 60 * 60 * 24));

  // Sort activities by row_number
  const sortedActivities = cityActivities.sort((a, b) => a.row_number - b.row_number);

  // Assign one activity per day based on number of nights
  let dayOffset = 0;

  for (let i = 0; i < sortedActivities.length && dayOffset < nightCount; i++) {
    const activity = sortedActivities[i];

    // Calculate activity day
    const activityDay = new Date(checkIn);
    activityDay.setDate(activityDay.getDate() + dayOffset);
    const activityDayStr = activityDay.toISOString().split('T')[0];

    // Keep activity as-is (combined or single) and just add activity_day
    results.push({
      json: {
        ...activity,
        activity_day: activityDayStr
      }
    });

    dayOffset++;
  }
}

return results;

