// Get the AI Agent output text
const inputData = $input.first().json;
const text = inputData.output || '';

// Parse the text to extract information
let country = 'Sri Lanka'; // Default fallback
let arrivalDate = '';
let adult = 0;
let children = 0;
let places = [];
let mealType = 'BB';

// Extract country (e.g., "Vietnam multi-city tour" or "Sri Lanka multi-city tour")
const countryMatch = text.match(/requesting a ([A-Za-z\s]+?)\s+multi-city tour/i);
if (countryMatch) {
  country = countryMatch[1].trim();
}

// Extract dates (e.g., "from December 12 to December 18, 2025")
const dateMatch = text.match(/from ([A-Za-z]+ \d+) to ([A-Za-z]+ \d+), (\d{4})/i);
if (dateMatch) {
  const monthDay = dateMatch[1]; // e.g., "December 12"
  const year = dateMatch[3];
  const date = new Date(`${monthDay}, ${year}`);
  arrivalDate = date.toISOString().split('T')[0];
}

// If no date found, use today + 15 days as default
if (!arrivalDate) {
  const defaultDate = new Date();
  defaultDate.setDate(defaultDate.getDate() + 15);
  arrivalDate = defaultDate.toISOString().split('T')[0];
}

// Extract pax (e.g., "for 3 adults and 2 children")
const adultMatch = text.match(/(\d+)\s+adult/i);
if (adultMatch) {
  adult = parseInt(adultMatch[1]);
}

const childMatch = text.match(/(\d+)\s+child/i);
if (childMatch) {
  children = parseInt(childMatch[1]);
}

// Extract places (e.g., "including Colombo, Kandy, Galle, and Yala")
const placesMatch = text.match(/including ([^.]+)\.?$/i);
if (placesMatch) {
  const placesStr = placesMatch[1];
  // Split by comma and 'and', then clean up
  places = placesStr
    .split(/,|\s+and\s+/)
    .map(p => p.trim())
    .filter(p => p.length > 0);
}

// Extract meal plan
if (text.toLowerCase().includes('bed & breakfast') || text.toLowerCase().includes('bed and breakfast')) {
  mealType = 'BB';
} else if (text.toLowerCase().includes('half board')) {
  mealType = 'HB';
} else if (text.toLowerCase().includes('full board')) {
  mealType = 'FB';
} else if (text.toLowerCase().includes('room only')) {
  mealType = 'RO';
} else if (text.toLowerCase().includes('all inclusive')) {
  mealType = 'AL';
}

// Create the formatted output
const output = {
  country_data: {
    country: country
  },
  genaral_data: {
    arrival_date: arrivalDate,
    pax: {
      adult: adult,
      cwb: children,
      cnb: 0
    },
    market: 1
  },
  destination_data: {
    place: places,
    meal_type: mealType
  }
};

return { json: output };

// Get ALL split items
const items = $input.all();
const originalData = $('Code').first().json;

// Get arrival date and calculate stays
const arrivalDate = originalData.genaral_data.arrival_date;
const totalNights = 6; // Default 7 days, 6 nights
const cityCount = items.length;
const nightsPerCity = Math.floor(totalNights / cityCount);

// Process each item and calculate check-in/check-out dates
const results = [];
let currentCheckIn = new Date(arrivalDate);

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const currentPlace = item.json['destination_data.place'];

  // Calculate check-out date (check-in + nights per city)
  const checkOutDate = new Date(currentCheckIn);
  checkOutDate.setDate(checkOutDate.getDate() + nightsPerCity);

  // Format dates as YYYY-MM-DD
  const checkIn = currentCheckIn.toISOString().split('T')[0];
  const checkOut = checkOutDate.toISOString().split('T')[0];

  results.push({
    json: {
      current_city: currentPlace,
      country_data: originalData.country_data,
      genaral_data: {
        pax: originalData.genaral_data.pax,
        market: originalData.genaral_data.market
      },
      destination_data: {
        meal_type: originalData.destination_data.meal_type,
        current_place: currentPlace,
        check_in: checkIn,
        check_out: checkOut
      }
    }
  });

  // Next city's check-in is current city's check-out
  currentCheckIn = checkOutDate;
}

// Add arrival_date to the first item only
if (results.length > 0) {
  results[0].json.genaral_data.arrival_date = arrivalDate;
}

return results;

