// Extract data from the previous node
const extractedData = $input.first().json;

// Get country
const country = extractedData.country_data?.country || 'Sri Lanka';

// Get general data
const arrivalDate = extractedData.genaral_data?.arrival_date || new Date(Date.now() + 15 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
const adult = extractedData.genaral_data?.pax?.adult || 0;
const cwb = extractedData.genaral_data?.pax?.cwb || 0;
const cnb = extractedData.genaral_data?.pax?.cnb || 0;
const market = extractedData.genaral_data?.market || 1;

// Get destination data
const places = extractedData.destination_data?.place || [];
const mealType = extractedData.destination_data?.meal_type || 'BB';

// Create the formatted output
const output = {
  country_data: {
    country: country
  },
  genaral_data: {
    arrival_date: arrivalDate,
    pax: {
      adult: adult,
      cwb: cwb,
      cnb: cnb
    },
    market: market
  },
  destination_data: {
    place: places,
    meal_type: mealType
  }
};

return { json: output };
