Generate the HOTELS-ONLY itinerary now!`;

// Build the OpenAI API request body
const requestBody = {
  model: "gpt-4o",
  messages: [
    {
      role: "system",
      content: "You are an expert travel planner. Create detailed day-by-day hotel itineraries in JSON format only. DO NOT include activities."
    },
    {
      role: "user",
      content: userPrompt
    }
  ],
  temperature: 0.7
};

// Return data with the request body and original input data
return [{
  json: {
    ...inputData,
    openai_request: requestBody
  }
}];

// Parse OpenAI itinerary response and format for cart processing (HOTELS ONLY)
// CRITICAL: Group hotels by city - ONE HOTEL PER CITY, not per day
const openAIResponse = $input.first().json.choices[0].message.content;
const inputData = $('Sanitize Data').first().json;

let itineraryData;
try {
  // Remove markdown code blocks if present
  let jsonString = openAIResponse.trim();
  if (jsonString.startsWith('```json')) {
    jsonString = jsonString.replace(/```json\n?/g, '').replace(/```\n?/g, '');
  } else if (jsonString.startsWith('```')) {
    jsonString = jsonString.replace(/```\n?/g, '');
  }

  itineraryData = JSON.parse(jsonString);
} catch (error) {
  return [{
    json: {
      success: false,
      message: "Failed to parse OpenAI itinerary response",
      error: error.message,
      raw_response: openAIResponse
    }
  }];
}

// CRITICAL: Group days by city to create ONE hotel booking per city
const cityGroups = {};
const itineraryWithHotelsOnly = [];

itineraryData.itinerary.forEach(day => {
  const cityName = day.city;

  // Group days by city
  if (!cityGroups[cityName]) {
    cityGroups[cityName] = {
      days: [],
      accommodation: day.accommodation && day.accommodation !== null ? day.accommodation : null,
      pax: day.pax
    };
  }

  cityGroups[cityName].days.push({
    day: day.day,
    date: day.date
  });
});

// Build itinerary with ONE accommodation entry per city (not per day)
// Each city gets ONE hotel for ALL its nights
itineraryData.itinerary.forEach(day => {
  const dayData = {
    day: day.day,
    date: day.date,
    city: day.city,
    pax: day.pax
  };

  // Only add accommodation for the FIRST day of each city
  const cityName = day.city;
  const cityDays = cityGroups[cityName].days;
  const isFirstDayInCity = cityDays[0].day === day.day;

  if (isFirstDayInCity && day.accommodation && day.accommodation !== null) {
    // This is the first day in this city - add accommodation for the entire stay
    dayData.accommodation = day.accommodation;
  } else if (!day.accommodation || day.accommodation === null) {
    // Last day - no accommodation
    dayData.accommodation = [];
  }
  // Other days in the same city: NO accommodation field (will be handled by Laravel)

  itineraryWithHotelsOnly.push(dayData);
});

// Generate itinerary title from travel data
const travelData = inputData.travel_data;
const destination = travelData.destination?.[0] || 'Unknown';
const duration = travelData.duration || '';
const startDate = travelData.travel_dates?.start || '';

// Get the unique cart_reference generated at the start of the flow
const cartReference = inputData.cart_reference;
const requestId = inputData.request_id;

// Generate RANDOM SUITABLE CART NAME for each request
const cartNamePrefixes = [
  'Adventure', 'Explorer', 'Wanderer', 'Voyager', 'Journey', 'Traveler',
  'Discovery', 'Expedition', 'Paradise', 'Escape', 'Gateway', 'Odyssey',
  'Quest', 'Retreat', 'Safari', 'Tour', 'Venture', 'Holiday', 'Getaway'
];

const cartNameSuffixes = [
  'Dream', 'Experience', 'Journey', 'Adventure', 'Escape', 'Paradise',
  'Discovery', 'Quest', 'Expedition', 'Voyage', 'Trip', 'Tour', 'Plan',
  'Package', 'Itinerary', 'Holiday', 'Getaway', 'Memories', 'Bliss'
];

const randomPrefix = cartNamePrefixes[Math.floor(Math.random() * cartNamePrefixes.length)];
const randomSuffix = cartNameSuffixes[Math.floor(Math.random() * cartNameSuffixes.length)];
const randomNumber = Math.floor(Math.random() * 999) + 1;

// Create a unique, human-readable cart name
const cartName = `${randomPrefix} ${destination} ${randomSuffix} #${randomNumber}`;
const itineraryTitle = `${cartName} - ${duration} (${startDate})`;

// Format response to match Laravel API structure
const formattedResponse = {
  success: true,
  message: "Day-wise hotel itinerary generated successfully",
  itinerary_title: itineraryTitle,
  cart_name: cartName,  // Human-readable cart name
  cart_reference: cartReference,  // Use cart reference from beginning of flow
  request_id: requestId,  // Request tracking ID
  itinerary: itineraryWithHotelsOnly,
  travel_data: inputData.travel_data,
  user_id: inputData.user_id,
  force_new_cart: true,  // Always create new cart for each request
  metadata: {
    total_days: itineraryWithHotelsOnly.length,
    total_hotels: itineraryWithHotelsOnly.filter(day => day.accommodation && Array.isArray(day.accommodation) && day.accommodation.length === 0 ? false : day.accommodation).length,
    generated_at: new Date().toISOString(),
    generation_method: "OpenAI GPT-4o",
    note: "Hotels only - no activities included",
    cart_reference: cartReference,
    cart_name: cartName,
    request_id: requestId
  }
};

return [{
  json: formattedResponse
}];

const cartTitle = $input.first().json.cart.cart_title;

return [{
  json: {
    message: `"${cartTitle}" is created. Check your itinerary.`
  }
}];

// Format AI Agent output for OpenAI Travel Parser
// CRITICAL: This node ensures AI Agent output is properly formatted
// The OpenAI Travel Parser will re-analyze this to ensure correct night calculations
const aiAgentOutput = $input.first().json.output || $input.first().json.text || '';

// Clean the output - remove any potential formatting issues
const cleanedOutput = String(aiAgentOutput).trim();

return [{
  json: {
    output: cleanedOutput,
    emailContent: cleanedOutput
  }
}];
