// Extract email_content and user_id from webhook body
const emailContent = $input.first().json.body?.email_content || $input.first().json.email_content || '';
const userId = $input.first().json.body?.user_id || $input.first().json.user_id || 'default-user';

return [{
  json: {
    chatInput: emailContent,
    sessionId: userId
  }
}];

// Merge data from chat trigger (direct) or webhook (via Extract Webhook Data)
const chatInput = $input.first().json.chatInput;
let sessionId = $input.first().json.sessionId;

// ============================================
// 🔧 SESSION ID CONFIGURATION (Applied to BOTH Hotels and Activities)
// ============================================
// OPTION 1: To test with specific ID, UNCOMMENT the line below and change the value:
   sessionId = "655";  // Change this to "1", "43", "655", "1234", etc.

// OPTION 2: To use actual input sessionId from webhook/chat, keep the line above COMMENTED
// ============================================
// Current behavior: Uses actual input sessionId (from webhook or chat)
// ============================================

// Ensure sessionId is an integer
if (!sessionId || sessionId === 'chat-session-' + Date.now()) {
  sessionId = 1;  // Default sessionId if none provided
} else if (typeof sessionId === 'string') {
  const parsed = parseInt(sessionId);
  sessionId = isNaN(parsed) ? 1 : parsed;
} else if (typeof sessionId !== 'number') {
  sessionId = 1;
}

// CRITICAL: Generate unique request ID and cart reference for EACH request
// This ensures both hotels and activities go into the SAME cart
const timestamp = Date.now();
const randomSuffix = Math.random().toString(36).substring(2, 9).toUpperCase();
const requestId = `REQ_${timestamp}_${randomSuffix}`;
const cartReference = `CART_${timestamp}_${randomSuffix}`;

return [{
  json: {
    chatInput: chatInput,
    sessionId: sessionId,  // SAME sessionId used for both Hotels and Activities workflows
    request_id: requestId,
    cart_reference: cartReference,  // SAME cart reference for both branches
    request_timestamp: new Date().toISOString()
  }
}];

// Format AI Agent output for OpenAI Travel Parser
const aiAgentOutput = $input.first().json.output || $input.first().json.text || '';
const cleanedOutput = String(aiAgentOutput).trim();

return [{
  json: {
    output: cleanedOutput,
    emailContent: cleanedOutput
  }
}];

// Parse OpenAI JSON response
const openAIResponse = $input.first().json.choices[0].message.content;
const inputData = $('Format for OpenAI Parser').first().json;

let travelData;
try {
  let jsonString = openAIResponse.trim();
  if (jsonString.startsWith('```json')) {
    jsonString = jsonString.replace(/```json\n?/g, '').replace(/```\n?/g, '');
  } else if (jsonString.startsWith('```')) {
    jsonString = jsonString.replace(/```\n?/g, '');
  }
  travelData = JSON.parse(jsonString);
} catch (error) {
  return [{
    json: {
      success: false,
      message: "Failed to parse OpenAI response",
      error: error.message,
      raw_response: openAIResponse
    }
  }];
}

// Remove country name from destination array
if (travelData.destination && Array.isArray(travelData.destination)) {
  const countries = [
    'Sri Lanka', 'Thailand', 'Singapore', 'Malaysia', 'Indonesia', 'Vietnam',
    'Philippines', 'Myanmar', 'Cambodia', 'Laos', 'India', 'China', 'Japan',
    'South Korea', 'UAE', 'United Arab Emirates', 'Maldives', 'Nepal', 'Bhutan'
  ];
  travelData.destination = travelData.destination.filter(dest => {
    return !countries.some(country => 
      dest.toLowerCase().trim() === country.toLowerCase().trim()
    );
  });
}

const formattedResponse = {
  success: true,
  message: "Travel itinerary parsed successfully",
  travel_data: travelData,
  suggestions: [
    `Optimized route: ${travelData.optimized_route?.join(' → ') || 'Route calculated'}`,
    `${travelData.city_stays?.length || 0} cities with ${travelData.duration || 'planned'} duration`
  ],
  metadata: {
    parsed_at: new Date().toISOString(),
    destinations_count: travelData.destination?.length || 0
  }
};

return [{
  json: formattedResponse
}];

// Add cart reference and generate SINGLE cart_title for BOTH branches
const travelData = $input.first().json;
const mergeInputData = $('Merge Input Sources').first().json;

let userId = mergeInputData.sessionId;
if (typeof userId === 'string') {
  const numMatch = userId.match(/\d+/);
  userId = numMatch ? parseInt(numMatch[0]) : 1;
} else if (typeof userId !== 'number') {
  userId = 1;
}

// CRITICAL: Generate SINGLE cart_title for BOTH hotels and activities
const destination = travelData.travel_data?.destination?.[0] || 'Unknown';
const duration = travelData.travel_data?.duration || '';
const startDate = travelData.travel_data?.travel_dates?.start || '';

// Generate RANDOM SUITABLE CART NAME - SAME for both hotels and activities
const cartNamePrefixes = [
  'Adventure', 'Explorer', 'Wanderer', 'Voyager', 'Journey', 'Traveler',
  'Discovery', 'Expedition', 'Paradise', 'Escape', 'Gateway', 'Odyssey',
  'Quest', 'Retreat', 'Safari', 'Tour', 'Venture', 'Holiday', 'Getaway'
];

const cartNameSuffixes = [
  'Dream', 'Experience', 'Journey', 'Adventure', 'Escape', 'Paradise',
  'Discovery', 'Quest', 'Expedition', 'Voyage', 'Trip', 'Tour', 'Plan',
  'Package', 'Itinerary', 'Holiday', 'Getaway', 'Memories', 'Bliss'
];

const randomPrefix = cartNamePrefixes[Math.floor(Math.random() * cartNamePrefixes.length)];
const randomSuffix = cartNameSuffixes[Math.floor(Math.random() * cartNameSuffixes.length)];
const randomNumber = Math.floor(Math.random() * 999) + 1;

// Create SINGLE cart name and title for BOTH branches
const cartName = `${randomPrefix} ${destination} ${randomSuffix} #${randomNumber}`;
const cartTitle = `${cartName} - ${duration} (${startDate})`;

const dataWithMetadata = {
  ...travelData,
  user_id: userId,
  cart_reference: mergeInputData.cart_reference,  // SAME cart for both branches
  request_id: mergeInputData.request_id,
  request_timestamp: mergeInputData.request_timestamp,
  cart_name: cartName,  // SAME cart name for both branches
  cart_title: cartTitle  // SAME cart title for both branches - CRITICAL!
};

return [{
  json: dataWithMetadata
}];

// Process travel data: set hotel star and create destination array for ALL 3 cities
const httpRequest1Data = $input.first().json;

// Create a deep copy to avoid mutation
let processedData = JSON.parse(JSON.stringify(httpRequest1Data));

// Extract hotel star rating from hotel_category
if (processedData.travel_data && processedData.travel_data.hotel_category) {
  const hotelCategory = processedData.travel_data.hotel_category;

  // Extract number from strings like "3-Star", "4-Star", "5-Star"
  const starMatch = hotelCategory.match(/(\d+)/);

  if (starMatch) {
    processedData.travel_data.hotel_star = parseInt(starMatch[1]);
  } else {
    // Default to 4 star if no number found
    processedData.travel_data.hotel_star = 4;
  }
} else {
  // Default to 4 star if hotel_category is not mentioned
  if (!processedData.travel_data) {
    processedData.travel_data = {};
  }
  processedData.travel_data.hotel_star = 4;
}

// CRITICAL: Build destination array from city_stays for ALL 3 cities
// Each city needs hotel booking - if staying multiple nights, it's ONE hotel for all nights
if (processedData.travel_data && processedData.travel_data.city_stays && Array.isArray(processedData.travel_data.city_stays)) {
  const cityStays = processedData.travel_data.city_stays;

  // Build destination array with ALL cities from optimized route
  // Each city appears ONCE even if staying multiple nights (same hotel for all nights)
  const destinations = cityStays.map(cityStay => cityStay.city);

  // Update destination array with all 3 cities
  processedData.travel_data.destination = destinations;

  // Create detailed city information including nights per city
  processedData.travel_data.city_details = cityStays.map(cityStay => ({
    city: cityStay.city,
    nights: cityStay.nights,
    order: cityStay.order,
    check_in_day: cityStay.check_in_day,
    check_out_day: cityStay.check_out_day,
    coordinates: cityStay.coordinates,
    accommodation_type: cityStay.accommodation_type,
    hotel_booking_note: cityStay.nights > 1 
      ? `Book ONE hotel for ${cityStay.nights} nights in ${cityStay.city}` 
      : `Book ONE hotel for ${cityStay.nights} night in ${cityStay.city}`
  }));

  // Store important metadata
  processedData.travel_data.total_cities = cityStays.length;
  processedData.travel_data.total_nights = cityStays.reduce((sum, city) => sum + city.nights, 0);
  processedData.travel_data.hotel_booking_summary = {
    total_cities: cityStays.length,
    total_hotels_needed: cityStays.length, // One hotel per city
    booking_details: cityStays.map(city => `${city.city}: ${city.nights} night(s) in ONE hotel`)
  };
}

return [{
  json: processedData
}];

// Process travel data: extract activity preferences and create city-wise activity data
const httpRequest1Data = $input.first().json;

// Create a deep copy to avoid mutation
let processedData = JSON.parse(JSON.stringify(httpRequest1Data));

// Extract activity preferences (replaces hotel star rating)
if (processedData.travel_data) {
  // Set activity preferences from budget level or use default
  processedData.travel_data.activity_preferences = processedData.travel_data.activity_preferences || 'popular_attractions';
  processedData.travel_data.budget_level = processedData.travel_data.budget_level || 'mid-range';
}

// CRITICAL: Build destination array and activity focus for each city
if (processedData.travel_data && processedData.travel_data.city_stays && Array.isArray(processedData.travel_data.city_stays)) {
  const cityStays = processedData.travel_data.city_stays;

  // Build destination array with ALL cities
  const destinations = cityStays.map(cityStay => cityStay.city);
  processedData.travel_data.destination = destinations;

  // Create detailed city information for activities
  // UPDATED: days_in_city = nights + 1 to include checkout day with activities
  processedData.travel_data.city_details = cityStays.map(cityStay => ({
    city: cityStay.city,
    nights: cityStay.nights,
    order: cityStay.order,
    check_in_day: cityStay.check_in_day,
    check_out_day: cityStay.check_out_day,
    coordinates: cityStay.coordinates,
    activity_focus: 'top_attractions',
    days_in_city: cityStay.nights + 1  // CHANGED: Activities for ALL days including checkout day
  }));

  // Store important metadata
  processedData.travel_data.total_cities = cityStays.length;
  processedData.travel_data.total_nights = cityStays.reduce((sum, city) => sum + city.nights, 0);
  processedData.travel_data.activity_summary = {
    total_cities: cityStays.length,
    total_activity_days: cityStays.reduce((sum, city) => sum + city.nights + 1, 0),  // CHANGED: Activity days = nights + 1 (includes checkout day)
    activity_details: cityStays.map(city => `${city.city}: ${city.nights + 1} days of activities (2 per day)`)  // CHANGED: Display actual activity days
  };
}

return [{
  json: processedData
}];

// Merge Google Sheets data with processed activities data - FILTER BY REQUESTED CITIES ONLY
// Include ALL activity parameters from Google Sheets
const processedData = $('Process Cities & Activities').first().json;
const sheetsData = $input.all();

// Get the list of cities from Process Cities & Activities
const requestedCities = processedData.travel_data?.destination || [];

// Extract ALL activity parameters from Google Sheets (Sheet4)
// HANDLE COMMA-SEPARATED VALUES: Split Activity Names, Aahaas ID, and Apple ID
const sheetActivities = [];

sheetsData.forEach(row => {
  const country = row.json.Country || row.json.country;
  const city = row.json.City || row.json.city;
  const lifestyleCategory = row.json['Lifestyle Category'] || row.json['Lifestyle  Category'] || row.json.lifestyle_category;
  const mustDoActivities = row.json['Must Do Activity'] || row.json.must_do_activity;
  const aahaasIds = row.json['Aahaas ID'] || row.json['Aahaas  ID'] || row.json.aahaas_id;
  const appleIds = row.json['Apple ID'] || row.json.apple_id;
  const extra1 = row.json['Extra1'] || row.json.extra1;
  const extra2 = row.json['Extra2'] || row.json.extra2;

  // Split comma-separated values and trim whitespace (handles both ", " and ",")
  const activityNameArray = String(mustDoActivities || '').split(',').map(name => name.trim()).filter(name => name);
  const aahaasIdArray = String(aahaasIds || '').split(',').map(id => id.trim()).filter(id => id);
  const appleIdArray = String(appleIds || '').split(',').map(id => id.trim()).filter(id => id);

  // Determine the maximum count to ensure we create entries for all data
  const maxCount = Math.max(activityNameArray.length, aahaasIdArray.length, appleIdArray.length);

  if (maxCount > 0) {
    // Create an activity entry for each index position
    for (let i = 0; i < maxCount; i++) {
      const activityName = activityNameArray[i] || activityNameArray[0] || '';
      const aahaasId = aahaasIdArray[i] || '';
      const appleId = appleIdArray[i] || '';

      // Only create entry if we have at least a name or an ID
      if (activityName || aahaasId || appleId) {
        sheetActivities.push({
          country: country,
          city: city,
          lifestyle_category: lifestyleCategory,
          must_do_activity: activityName,
          aahaas_id: aahaasId,
          apple_id: appleId,
          extra1: extra1,
          extra2: extra2
        });
      }
    }
  } else {
    // If no data at all, create one entry with empty values
    sheetActivities.push({
      country: country,
      city: city,
      lifestyle_category: lifestyleCategory,
      must_do_activity: '',
      aahaas_id: '',
      apple_id: '',
      extra1: extra1,
      extra2: extra2
    });
  }
});

// CRITICAL: Filter activities to ONLY include cities from the itinerary
const filteredActivities = sheetActivities.filter(item => {
  if (!item.city) return false;

  // Check if this city is in the requested cities list (case-insensitive)
  return requestedCities.some(requestedCity => 
    requestedCity.toLowerCase().trim() === item.city.toLowerCase().trim()
  );
});

// Add filtered Google Sheets data to processed data
processedData.google_sheets_activities = filteredActivities;

// Group activities by city from Google Sheets (only for requested cities)
// Include ALL activity parameters in the output
const activitiesByCity = {};
filteredActivities.forEach(item => {
  const city = item.city;
  if (city && item.must_do_activity) {
    if (!activitiesByCity[city]) {
      activitiesByCity[city] = [];
    }
    activitiesByCity[city].push({
      lifestyle_category: item.lifestyle_category,
      must_do_activity: item.must_do_activity,
      aahaas_id: item.aahaas_id,
      apple_id: item.apple_id,
      extra1: item.extra1,
      extra2: item.extra2,
      country: item.country,
      city: item.city
    });
  }
});

processedData.activities_by_city = activitiesByCity;

// Add metadata about filtering
processedData.activity_metadata = {
  requested_cities: requestedCities,
  total_sheet_activities: sheetActivities.length,
  filtered_activities_count: filteredActivities.length,
  cities_with_activities: Object.keys(activitiesByCity),
  cities_without_activities: requestedCities.filter(city => !activitiesByCity[city])
};

return [{
  json: processedData
}];

// Sanitize data for Hotels API and validate required fields
const inputData = $input.first().json;

// CRITICAL: Validate that we have the required travel_data structure
if (!inputData.travel_data || typeof inputData.travel_data !== 'object') {
  throw new Error('Missing travel_data object. Data flow is broken. Check that "Merge Sheets Data1" is receiving data from "Process Cities & Hotel Rating".');
}

if (!inputData.user_id) {
  throw new Error('Missing user_id. Check data flow from "Add Cart Metadata".');
}

if (!inputData.cart_reference) {
  throw new Error('Missing cart_reference. Check data flow from "Add Cart Metadata".');
}

// WARNING: Log if destination or city_details are missing, but don't fail
// These will be populated by OpenAI if missing
if (!inputData.travel_data.destination || inputData.travel_data.destination.length === 0) {
  console.warn('WARNING: destination is empty in travel_data. OpenAI will generate it.');
  // Initialize empty array if missing
  if (!inputData.travel_data.destination) {
    inputData.travel_data.destination = [];
  }
}

if (!inputData.travel_data.city_details || inputData.travel_data.city_details.length === 0) {
  console.warn('WARNING: city_details is empty in travel_data. OpenAI will generate it.');
  // Initialize empty array if missing
  if (!inputData.travel_data.city_details) {
    inputData.travel_data.city_details = [];
  }
}

function cleanString(str) {
  if (typeof str !== 'string') return str;
  return str.replace(/[\x00-\x08\x0B-\x0C\x0E-\x1F\x7F]/g, '');
}

function cleanObject(obj) {
  if (obj === null || obj === undefined) return obj;
  if (typeof obj === 'string') return cleanString(obj);
  if (Array.isArray(obj)) return obj.map(item => cleanObject(item));
  if (typeof obj === 'object') {
    const cleaned = {};
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) cleaned[key] = cleanObject(obj[key]);
    }
    return cleaned;
  }
  return obj;
}

return [{ json: cleanObject(inputData) }];

// Sanitize data for Activities API and validate required fields
const inputData = $input.first().json;

// CRITICAL: Validate that we have the required travel_data structure
if (!inputData.travel_data || typeof inputData.travel_data !== 'object') {
  throw new Error('Missing travel_data object. Data flow is broken. Check that "Merge Sheets Data" is receiving data from "Process Cities & Activities".');
}

if (!inputData.user_id) {
  throw new Error('Missing user_id. Check data flow from "Add Cart Metadata".');
}

if (!inputData.cart_reference) {
  throw new Error('Missing cart_reference. Check data flow from "Add Cart Metadata".');
}

// WARNING: Log if destination or city_details are missing, but don't fail
// These will be populated by OpenAI if missing
if (!inputData.travel_data.destination || inputData.travel_data.destination.length === 0) {
  console.warn('WARNING: destination is empty in travel_data. OpenAI will generate it.');
  // Initialize empty array if missing
  if (!inputData.travel_data.destination) {
    inputData.travel_data.destination = [];
  }
}

if (!inputData.travel_data.city_details || inputData.travel_data.city_details.length === 0) {
  console.warn('WARNING: city_details is empty in travel_data. OpenAI will generate it.');
  // Initialize empty array if missing
  if (!inputData.travel_data.city_details) {
    inputData.travel_data.city_details = [];
  }
}

function cleanString(str) {
  if (typeof str !== 'string') return str;
  return str.replace(/[\x00-\x08\x0B-\x0C\x0E-\x1F\x7F]/g, '');
}

function cleanObject(obj) {
  if (obj === null || obj === undefined) return obj;
  if (typeof obj === 'string') return cleanString(obj);
  if (Array.isArray(obj)) return obj.map(item => cleanObject(item));
  if (typeof obj === 'object') {
    const cleaned = {};
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) cleaned[key] = cleanObject(obj[key]);
    }
    return cleaned;
  }
  return obj;
}

return [{ json: cleanObject(inputData) }];

// Build OpenAI prompt for HOTELS-ONLY itinerary generation
const inputData = $input.first().json;
const data = inputData.travel_data || {};

const destination = data.destination?.[0] || 'Unknown';
const startDate = data.travel_dates?.start || '';
const endDate = data.travel_dates?.end || '';
const duration = data.duration || '';
const adults = data.pax?.adults || 1;
const children = data.pax?.children || 0;
const childAges = data.pax?.child_ages || [];
const mealPlan = data.meal_plan || 'BB';
const hotelCategory = data.hotel_category || '4-Star';
const starCategory = data.hotel_star || 4;

// Get cities from city_details
const cities = data.city_details || [];
const citiesStr = cities.map(c => `${c.city} (${c.nights} nights, Days ${c.check_in_day}-${c.check_out_day})`).join(', ');

// Calculate total days
const start = new Date(startDate);
const end = new Date(endDate);
const totalDays = Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;

// Build city stays breakdown text
const cityStaysBreakdown = cities.map(c => `- ${c.city}: ${c.nights} nights (Check-in Day ${c.check_in_day}, Check-out Day ${c.check_out_day}) at ${c.coordinates}`).join('
');

// Build city planning section
let cityPlanningText = '- Respect the city stays schedule
';
if (cities[0]) {
  cityPlanningText += `   - Day ${cities[0].check_in_day}-${cities[0].check_out_day}: Stay in ${cities[0].city}
`;
}
if (cities[1]) {
  cityPlanningText += `   - Day ${cities[1].check_in_day}-${cities[1].check_out_day}: Stay in ${cities[1].city}
`;
}
if (cities[2]) {
  cityPlanningText += `   - Day ${cities[2].check_in_day}-${cities[2].check_out_day}: Stay in ${cities[2].city}
`;
}

// Build example itinerary text
const exampleItinerary = cities.map(city => {
  const startDay = city.check_in_day;
  const endDay = city.check_out_day;
  const nights = city.nights;
  return `Days ${startDay}-${endDay - 1} (${city.city} - ${nights} nights):
- Accommodation in ${city.city}
- Coordinates near ${city.coordinates}`;
}).join('

');

const lastCity = cities[cities.length - 1]?.city || 'last city';
const citiesRoute = cities.map(c => c.city).join(' → ');

// Build the complete prompt
const userPrompt = `You are an expert travel planner. Create a detailed day-by-day itinerary for a ${totalDays}-day trip.

IMPORTANT: HOTELS/ACCOMMODATION ONLY - DO NOT include activities!

TRIP DETAILS:
- Cities: ${citiesStr}
- Duration: ${duration} (${startDate} to ${endDate})
- Total Days: ${totalDays}
- Travelers: ${adults} adults, ${children} children
- Child Ages: ${JSON.stringify(childAges)}
- Meal Plan: ${mealPlan}
- Hotel Category: ${hotelCategory} (${starCategory}-Star)

CITY STAYS BREAKDOWN:
${cityStaysBreakdown}

CRITICAL REQUIREMENTS:

1. HOTELS ONLY - NO ACTIVITIES:
   - DO NOT include activities array
   - DO NOT include activity details
   - ONLY provide accommodation information per day

2. PAX OBJECT (MANDATORY):
   - EVERY day MUST include pax object: {"adults": ${adults}, "children": ${children}, "child_ages": ${JSON.stringify(childAges)}}

3. ONE HOTEL PER CITY FOR ALL NIGHTS:
   - Each city gets ONE hotel booking for ALL nights in that city
   - Example: 10 days 9 nights, 3 cities (9÷3=3 nights each):
     * City 1 (Days 1-3): ONE hotel, check_in Day 1, check_out Day 4
       - Day 1: accommodation with check_in="2025-12-15", check_out="2025-12-18"
       - Day 2: accommodation with SAME check_in="2025-12-15", check_out="2025-12-18"
       - Day 3: accommodation with SAME check_in="2025-12-15", check_out="2025-12-18"
     * City 2 (Days 4-6): ONE hotel, check_in Day 4, check_out Day 7
       - Day 4: accommodation with check_in="2025-12-18", check_out="2025-12-21"
       - Day 5: accommodation with SAME check_in="2025-12-18", check_out="2025-12-21"
       - Day 6: accommodation with SAME check_in="2025-12-18", check_out="2025-12-21"
     * City 3 (Days 7-9): ONE hotel, check_in Day 7, check_out Day 10
       - Day 7: accommodation with check_in="2025-12-21", check_out="2025-12-24"
       - Day 8: accommodation with SAME check_in="2025-12-21", check_out="2025-12-24"
       - Day 9: accommodation with SAME check_in="2025-12-21", check_out="2025-12-24"
   - All days in same city MUST have IDENTICAL accommodation object
   - Check-in date = First day of city stay
   - Check-out date = Last day of city stay + 1 day

4. ACCOMMODATION (MANDATORY FIELDS):
   - check_in: date (YYYY-MM-DD) - FIRST day of stay in that city
   - check_out: date (YYYY-MM-DD) - LAST day of stay + 1 day in that city
   - star_category: ${starCategory} (integer)
   - keywords: ["${hotelCategory}", "${mealPlan}", city name, amenities like "pool", "wifi", "breakfast"]
   - latitude: number
   - longitude: number

5. CITY-BASED PLANNING:
${cityPlanningText}

REQUIRED JSON STRUCTURE (HOTELS ONLY):

{
  "itinerary": [
    {
      "day": 1,
      "date": "${startDate}",
      "city": "${cities[0]?.city || 'City Name'}",
      "pax": {
        "adults": ${adults},
        "children": ${children},
        "child_ages": ${JSON.stringify(childAges)}
      },
      "accommodation": {
        "check_in": "${startDate}",
        "check_out": "2025-12-16",
        "star_category": ${starCategory},
        "keywords": ["${hotelCategory}", "${mealPlan}", "city", "pool", "wifi"],
        "latitude": 7.2910,
        "longitude": 80.6350
      }
    }
  ]
