let totalActivitiesAfterLimit = 0;

const itineraryWithActivities = itineraryData.itinerary.map(day => {
  const activities = day.activities || [];
  totalActivitiesBeforeLimit += activities.length;

  // LIMIT: Take only first MAX_ACTIVITIES_PER_DAY activities
  const limitedActivities = activities.slice(0, MAX_ACTIVITIES_PER_DAY);
  totalActivitiesAfterLimit += limitedActivities.length;

  // Get Google Sheets activity suggestions for this city
  const cityName = day.city;
  const cityActivities = activitiesByCity[cityName] || [];

  // ANTI-DUPLICATE LOGIC: Track which activities have been used for this city
  if (!cityActivityTracker[cityName]) {
    cityActivityTracker[cityName] = {
      usedIndices: [],
      currentIndex: 0
    };
  }

  // Get unique activities for this day (not used in previous days of the same city)
  const uniqueActivitiesForThisDay = [];
  const tracker = cityActivityTracker[cityName];

  // Try to assign MAX_ACTIVITIES_PER_DAY unique activities from Google Sheets
  let assignedCount = 0;
  while (assignedCount < MAX_ACTIVITIES_PER_DAY && tracker.currentIndex < cityActivities.length) {
    const activity = cityActivities[tracker.currentIndex];

    // Check if this activity has already been used
    if (!tracker.usedIndices.includes(tracker.currentIndex)) {
      uniqueActivitiesForThisDay.push(activity);
      tracker.usedIndices.push(tracker.currentIndex);
      assignedCount++;
    }

    tracker.currentIndex++;
  }

  // If we've exhausted all activities, reset the tracker for cycling
  if (tracker.currentIndex >= cityActivities.length && assignedCount < MAX_ACTIVITIES_PER_DAY) {
    tracker.currentIndex = 0;
    tracker.usedIndices = [];
  }

  return {
    day: day.day,
    date: day.date || calculateDate(day.day, startDate),
    city: day.city,
    pax: day.pax,
    activities: limitedActivities,
