  // PASS THROUGH: Google Sheets activity parameters
  google_sheets_activities: googleSheetsActivities,
  activities_by_city: activitiesByCity,
  metadata: {
    ...data.metadata,
    total_activities: totalActivities,
    validated: true,
    validation_time: new Date().toISOString()
  }
};

return [{
  json: payload
}];

// Combine hotels and activities responses
const inputs = $input.all();
const hotelsResponse = inputs.find(i => i.json.cart?.cart_title);
const activitiesResponse = inputs.find(i => i.json.cart && !hotelsResponse) || inputs[1];

const cartTitle = hotelsResponse?.json.cart?.cart_title || 'Your Travel Cart';
const cartId = hotelsResponse?.json.cart?.id || activitiesResponse?.json.cart?.id;

return [{
  json: {
    message: `"${cartTitle}" has been created with both hotels and activities. Check your itinerary.`,
    cart_id: cartId,
    hotels_added: true,
    activities_added: true,
    success: true
  }
}];

// Merge Google Sheets hotel data with processed travel data
// Filter by cities from 'Get row(s) in sheet1' input side and include ALL hotel parameters
const processedData = $('Process Cities & Hotel Rating').first().json;
const sheetsData = $input.all();

// Get the list of cities from 'Get row(s) in sheet1' input (from Process Cities & Hotel Rating)
const requestedCities = processedData.travel_data?.destination || [];

// Extract ALL hotel parameters from Google Sheets (Sheet3)
// HANDLE COMMA-SEPARATED VALUES: Split Hotel Names, Aahaas Hotel ID, and Apple Hotel ID
const sheetHotels = [];

sheetsData.forEach(row => {
  const country = row.json.Country || row.json.country;
  const city = row.json.City || row.json.city;
  const hotelClass = row.json['Hotel Class'] || row.json.hotel_class;
  const hotelNames = row.json['Hotel Name'] || row.json.hotel_name;
  const aahaasIds = row.json['Aahaas Hotel ID'] || row.json.aahaas_hotel_id;
  const appleIds = row.json['Apple Hotel ID'] || row.json.apple_hotel_id;
  const basicRoomCategory = row.json['Basic Room Category'] || row.json.basic_room_category || row.json['Basic Room Categoty'];
  const deluxe = row.json['Deluxe'] || row.json.deluxe;

  // Split comma-separated values and trim whitespace (handles both ", " and ",")
  const hotelNameArray = String(hotelNames || '').split(',').map(name => name.trim()).filter(name => name);
  const aahaasIdArray = String(aahaasIds || '').split(',').map(id => id.trim()).filter(id => id);
  const appleIdArray = String(appleIds || '').split(',').map(id => id.trim()).filter(id => id);

  // Determine the maximum count to ensure we create entries for all data
  const maxCount = Math.max(hotelNameArray.length, aahaasIdArray.length, appleIdArray.length);

  if (maxCount > 0) {
    // Create a hotel entry for each index position
    for (let i = 0; i < maxCount; i++) {
      const hotelName = hotelNameArray[i] || hotelNameArray[0] || '';
      const aahaasId = aahaasIdArray[i] || '';
      const appleId = appleIdArray[i] || '';

      // Only create entry if we have at least a name or an ID
      if (hotelName || aahaasId || appleId) {
        sheetHotels.push({
          country: country,
          city: city,
          hotel_class: hotelClass,
          hotel_name: hotelName,
          aahaas_hotel_id: aahaasId,
          apple_hotel_id: appleId,
          basic_room_category: basicRoomCategory,
          deluxe: deluxe
        });
      }
    }
  } else {
    // If no data at all, create one entry with empty values
    sheetHotels.push({
      country: country,
      city: city,
      hotel_class: hotelClass,
      hotel_name: '',
      aahaas_hotel_id: '',
      apple_hotel_id: '',
      basic_room_category: basicRoomCategory,
      deluxe: deluxe
    });
  }
});

// CRITICAL: Filter hotels to ONLY include cities that are in the city array from input
const filteredHotels = sheetHotels.filter(item => {
  if (!item.city) return false;

  // Check if this city is in the requested cities list (case-insensitive)
  return requestedCities.some(requestedCity => 
    requestedCity.toLowerCase().trim() === item.city.toLowerCase().trim()
  );
});

// Add filtered Google Sheets hotel data to processed data
processedData.google_sheets_hotels = filteredHotels;

// Group hotels by city from Google Sheets (only for requested cities)
// Include ALL hotel parameters in the output
const hotelsByCity = {};
filteredHotels.forEach(item => {
  const city = item.city;
  if (city && item.hotel_name) {
    if (!hotelsByCity[city]) {
      hotelsByCity[city] = [];
    }
    hotelsByCity[city].push({
      hotel_class: item.hotel_class,
      hotel_name: item.hotel_name,
      aahaas_hotel_id: item.aahaas_hotel_id,
      apple_hotel_id: item.apple_hotel_id,
      basic_room_category: item.basic_room_category,
      deluxe: item.deluxe,
      country: item.country,
      city: item.city
    });
  }
});

processedData.hotels_by_city = hotelsByCity;

// Add metadata about filtering
processedData.hotel_metadata = {
  requested_cities: requestedCities,
  total_sheet_hotels: sheetHotels.length,
  filtered_hotels_count: filteredHotels.length,
  cities_with_hotels: Object.keys(hotelsByCity),
  cities_without_hotels: requestedCities.filter(city => !hotelsByCity[city])
};

return [{
  json: processedData
}];
