const travelData = JSON.parse($input.first().json.message.content);

// Generate tour session ID (timestamp-based)
const tourSession = Math.floor(Date.now() / 1000);

// Parse arrival date
const arrivalDate = travelData.arrival_date;

// Build the final JSON structure matching your exact format
const output = {
  "config": {
    "status": 1,
    "tour_session": tourSession,
    "tour_type": 3,
    "user": 116
  },
  "country_data": {
    "country": travelData.country
  },
  "general_data": {
    "arrival_date": arrivalDate,
    "pax": {
      "adult": travelData.pax.adult || 2,
      "cwb": travelData.pax.cwb || 0,
      "cnb": travelData.pax.cnb || 0
    },
    "market": travelData.market
  },
  "destination_data": {
    "place": travelData.places || []
  }
};

return { json: output };

